import React, { createContext, useEffect, useState } from "react";
import { Link, RouterProvider, createBrowserRouter } from "react-router-dom";
import Accounts from "./components/Accounts.jsx";
import SendEther from "./components/SendEther.jsx";
import Welcome from "./components/Welcome.jsx";
import getWeb3 from "./getWeb3";

const router = createBrowserRouter([
  { path: "/send-ether", element: <SendEtherWithContext /> }
]);

const AppContext = createContext();

function App() {
  const [web3, setWeb3] = useState(null);
  const [account, setAccount] = useState(null);

  function setAddress(address) {
    setAccount(address);
  }

  useEffect(() => {
    const init = async () => {
      try {
        const web3 = await getWeb3();
        setWeb3(web3);
      } catch (error) {
        alert("Failed to load web3 or contract.");
        console.error(error);
      }
    };
    init();
  }, []);

  return (
    <AppContext.Provider value={{ web3, account }}>
      <RouterProvider router={router}>
        <div className="Flex">
          <div className="welMargin">
            <Welcome />
          </div>
          <div className="Account">
            <Accounts web3={web3} setAccountAddress={setAddress} />
          </div>
          <Link to="/send-ether">
            <button className="btn">Send Ether</button>
          </Link>
        </div>
      </RouterProvider>
    </AppContext.Provider>
  );
}

export default App;

function SendEtherWithContext() {
  const { web3, account } = useContext(AppContext);
  return <SendEther web3={web3} account={account} />;
}
