import React, { useEffect, useState } from "react";
import { Link, Route, BrowserRouter as Router } from "react-router-dom";
import "./App.css";
import Accounts from "./components/Accounts.jsx";
import SendEther from "./components/SendEther.jsx";
import Welcome from "./components/Welcome.jsx";
import getWeb3 from "./getWeb3";


function App() {
  const [web3, setWeb3] = useState(null);
  const [account, setAccount] = useState(null);

  function setAddress(address) {
    setAccount(address);
  }

  useEffect(() => {
    const init = async () => {
      try {
        const web3 = await getWeb3();
        setWeb3(web3);
      } catch (error) {
        alert("Failed to load web3 or contract.");
        console.error(error);
      }
    };
    init();
  }, []);

  return (
    <Router>
      <div className="Flex">
        <div className="welMargin">
          <Welcome />
        </div>
        <div className="Account">
          <Accounts web3={web3} setAccountAddress={setAddress} />
        </div>
        <div>
          <Route path="/send-ether" component={() => <SendEther web3={web3} account={account} />} />
        </div>
        <Link to="/send-ether">
          <button className="btn">Send Ether</button>
        </Link>
      </div>
    </Router>
  );
}

export default App;
